import './App.css';
import { useState } from 'react';
import History from './History';

const doubleOperators = ['+', '-', '/', '*', '.'];
const databseURL = 'http://localhost:3500/history';

const CalcResult = ({ input, clearInput }) => {
  return (
    <tr>
      <td colSpan='3' id='calc-result'>{input}</td>
      <td onClick={clearInput} id='calc-clear'>c</td>
    </tr>
  );
};

const CalcButton = ({ onClick, value }) => {
  return <td onClick={onClick}>{value}</td>;
};

const NumberButtonsRow = ({ onClick }) => {
  return (
    <tr>
      <CalcButton onClick={() => onClick(1)} value={1} />
      <CalcButton onClick={() => onClick(2)} value={2} />
      <CalcButton onClick={() => onClick(3)} value={3} />
      <CalcButton onClick={() => onClick('+')} value='+' />
    </tr>
  );
};

const OperatorButtonsRow = ({ onClick }) => {
  return (
    <tr>
      <CalcButton onClick={() => onClick(4)} value={4} />
      <CalcButton onClick={() => onClick(5)} value={5} />
      <CalcButton onClick={() => onClick(6)} value={6} />
      <CalcButton onClick={() => onClick('-')} value='-' />
    </tr>
  );
};

const NumberButtonsRow2 = ({ onClick }) => {
  return (
    <tr>
      <CalcButton onClick={() => onClick(7)} value={7} />
      <CalcButton onClick={() => onClick(8)} value={8} />
      <CalcButton onClick={() => onClick(9)} value={9} />
      <CalcButton onClick={() => onClick('*')} value='*' />
    </tr>
  );
};

const FinalRow = ({ onClick }) => {
  return (
    <tr>
      <CalcButton onClick={() => onClick(0)} value={0} />
      <CalcButton onClick={() => onClick('.')} value='.' />
      <CalcButton onClick={onClick} value='=' />
      <CalcButton onClick={() => onClick('/')} value='/' />
    </tr>
  );
};

function App() {
  const [input, setInput] = useState('');

  const addHistoryRecord = async (newRecord) => {
    try {
      const response = await fetch(databseURL, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newRecord),
      });
      if (response.ok) {
        console.log('Ok');
      } else {
        console.error('Failed');
      }
    } catch (error) {
      console.error(error);
    }
  };

  const doubleOperatorCheck = (character) => {
    const lastInputCharacter = input.split('').pop();
    const checkCondition =
      doubleOperators.includes(lastInputCharacter) &&
      doubleOperators.includes(character);
    if (!checkCondition) {
      setInput(input + character);
    }
  };

  const calculationFunction = (inputString) => {
    const lastInputCharacter = input?.split('').pop();
    const checkCondition = doubleOperators.includes(lastInputCharacter);
    if (!checkCondition) {
      let result = Function(`return ${inputString}`)();
      setInput(`${result}`);

      const newHistoryRecord = { value: `${inputString}=${result}` };
      addHistoryRecord(newHistoryRecord);
    }
  };

  const clearInput = () => {
    setInput('');
  };

  return (
    <>
      <div id='container'>
        <div id='table'>
          <table id='calculator'>
            <tbody>
              <CalcResult input={input} clearInput={clearInput} />
              <NumberButtonsRow onClick={doubleOperatorCheck} />
              <OperatorButtonsRow onClick={doubleOperatorCheck} />
              <NumberButtonsRow2 onClick={doubleOperatorCheck} />
              <FinalRow onClick={() => {
                doubleOperatorCheck('=');
                calculationFunction(input);
                addHistoryRecord(input);
              }} />
            </tbody>
          </table>
        </div>
        <div id='history-container'>
          <h1>History</h1>
          <div id='history-content'>
            <History addHistoryRecord={addHistoryRecord} />
          </div>
        </div>
      </div>
    </>
  );
}

export default App;
