import './App.css';
import { useState } from 'react';



function App() {
  const [input,setInput] = useState('');

  const doubleOperatorCheck = (character) => {
    const doubleOperators = ['+','-','/','*','.']
    const lastInputCharacter = input?.split('').pop()

    const checkFunction = doubleOperators.includes(lastInputCharacter)
    if(!checkFunction){
      setInput(input+character)
    }

}

  const calculationFunction = (inputString) => {
    let result = Function(`return ${inputString}`)()
    setInput(result)
  }

  return (
    <div id="table">
      <table id="calculator">
        <tbody>
        <tr>
          <td colSpan="3" id="calc-result">{input}</td>
          <td onClick={()=>{setInput('')}}id="calc-clear">c</td>
        </tr>
        <tr>
          <td onClick={()=>{doubleOperatorCheck(1)}}>1</td>
          <td onClick={()=>{setInput(input+2)}}>2</td>
          <td onClick={()=>{setInput(input+3)}}>3</td>
          <td onClick={()=>{setInput(input+'+')}}>+</td>
        </tr>
        <tr>
          <td onClick={()=>{setInput(input+4)}}>4</td>
          <td onClick={()=>{setInput(input+5)}}>5</td>
          <td onClick={()=>{setInput(input+6)}}>6</td>
          <td onClick={()=>{setInput(input+'-')}}>-</td>
        </tr>
        <tr>
          <td onClick={()=>{setInput(input+7)}}>7</td>
          <td onClick={()=>{setInput(input+8)}}>8</td>
          <td onClick={()=>{setInput(input+9)}}>9</td>
          <td onClick={()=>{setInput(input+'*')}}>*</td>
        </tr>
        <tr>
          <td onClick={()=>{setInput(input+0)}}>0</td>
          <td onClick={()=>{setInput(input+'.')}}>.</td>
          <td onClick={()=>{calculationFunction(input)}}>=</td>
          <td onClick={()=>{setInput(input+'/')}}>/</td>
        </tr>
        </tbody>
      </table>
    </div>
  );
}

export default App;
