import './App.css';
import { useState } from 'react';
import History from './History';

const doubleOperators = ['+','-','/','*','.']
const databseURL = "http://localhost:3500/history"

const addHistoryRecord = async (newRecord) => {
  try {
    const response = await fetch(databseURL, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(newRecord)
      });
      if (response.ok) {
        console.log('Ok');
      } else {
        console.error('Failed');
      }
    } catch (error) {
      console.error(error);
    }
  };

  const doubleOperatorCheck = (character) => {
    const inputCopy = input.slice("")
    const lastInputCharacter = inputCopy.split('').pop()
    const checkCondition = doubleOperators.includes(lastInputCharacter) && doubleOperators.includes(character)
    if(!checkCondition){
      setInput(input+character)
    }
    return (input+character)
  }

  const calculationFunction = (inputString) => {
    const lastInputCharacter = input?.split('').pop()
    const checkCondition = doubleOperators.includes(lastInputCharacter)
    if(!checkCondition){
      let result = Function(`return ${inputString}`)()
      setInput(`${result}`);

      const newHistoryRecord = {value:`${inputString}=${result}`}
      addHistoryRecord(newHistoryRecord);
    }
  }

const Calculator = () => {

  const [input,setInput] = useState('')

  const handleClear = () => {
    setInput('');
  };

  const handleNumberClick = (number) => {
    const newInput = doubleOperatorCheck(input, number.toString());
    setInput(newInput);
  };

  const handleOperatorClick = (operator) => {
    const newInput = doubleOperatorCheck(input, operator);
    setInput(newInput);
  };

  const handleEqualsClick = () => {
    const result = calculationFunction(input);
    setInput(result);
  };

  return (
    <>
      <div id='container'>
          <div id='table'>
            <table id='calculator'>
            <tbody>
            <tr>
              <td colSpan='3' id='calc-result'>{input}</td>
              <td onClick={handleClear} id='calc-clear'>c</td>
            </tr>
            <tr>
              <td onClick={() => handleNumberClick(1)}>1</td>
              <td onClick={() => handleNumberClick(2)}>2</td>
              <td onClick={() => handleNumberClick(3)}>3</td>
              <td onClick={() => handleOperatorClick('+')}>+</td>
            </tr>
            <tr>
              <td onClick={() => handleNumberClick(4)}>4</td>
              <td onClick={() => handleNumberClick(5)}>5</td>
              <td onClick={() => handleNumberClick(6)}>6</td>
              <td onClick={() => handleOperatorClick('-')}>-</td>
            </tr>
            <tr>
              <td onClick={() => handleNumberClick(7)}>7</td>
              <td onClick={() => handleNumberClick(8)}>8</td>
              <td onClick={() => handleNumberClick(9)}>9</td>
              <td onClick={() => handleOperatorClick('*')}>*</td>
            </tr>
            <tr>
              <td onClick={() => handleNumberClick(0)}>0</td>
              <td onClick={() => handleOperatorClick('.')}>.</td>
              <td onClick={handleEqualsClick}>=</td>
              <td onClick={() => handleOperatorClick('/')}>/</td>
            </tr>
          </tbody>
            </table>
          </div>
          <div id='history-container'>
            <h1>History</h1>
              <div id='history-content'>
                <History addHistoryRecord={addHistoryRecord}/>
              </div>
          </div> 
        </div>
    </>
  );
}


function App() {

  return (
    <>
    <Calculator/>
    <div id='history-container'>
      <h1>History</h1>
        <div id='history-content'>
        <History addHistoryRecord={addHistoryRecord}/>
        </div>
      </div>
    </>
  );
}

export default App;
