import {
    useEffect,
    useState
} from 'react';

const History = ({
    addHistoryRecord
}) => {
    const [content, setContent] = useState([]);
    const databseURL = "http://localhost:3500/history";

    const fetchData = async () => {
        try {
            const response = await fetch(`${databseURL}?_limit=10`);
            const data = await response.json();
            return data;
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    useEffect(() => {
        fetchData().then(setContent)
    }, [addHistoryRecord]);

    if (content.length === 0) {
        return < p > loading history.. < /p>;
    }

    return content.map((record) => {
        return < p key = {
            record.id
        } > {
            record.value
        } < /p>;
    });
};

export default History;
